vertex.source:
#version 300 es

// Vertex position in object space coordinates
in vec3 vertexPosition;
// Surface normal at the vertex in object space coordinates
in vec3 vertexNormal;
// Texture coordinates at that vertex
in vec2 vertexTextureCoordinates;

out vec3 fragPos;
out vec3 fragN;
out vec3 camPos;

// Model matrix
uniform mat4 mMatrix;
// View matrix
uniform mat4 vMatrix;
// Projection matrix
uniform mat4 pMatrix;

// Main program for each vertex
void main() {
  vec4 vertexCamSpace = vMatrix * mMatrix * vec4(vertexPosition, 1.0);
  gl_Position = pMatrix * vertexCamSpace;

  fragPos = (mMatrix * vec4(vertexPosition, 1.0)).xyz;
  fragN = (transpose(inverse(mMatrix)) * vec4(vertexNormal, 0.0)).xyz;
  camPos = (inverse(vMatrix) * vec4(0.0, 0.0, 0.0, 1.0)).xyz;
}

fragment.source:
#version 300 es

// For better performance less precision
precision highp float;

in vec3 fragPos;
in vec3 fragN;
in vec3 camPos;

uniform vec3 lightDirection;
uniform vec3 lightColor;
uniform float kA; // 0.5
uniform float kD; // 1.0
uniform float kS; // 0.5
uniform float s; // 20
uniform float Ia; // 0.5

out vec4 fragColor;

// Main program for each fragment = pixel candidate
void main() {
  vec3 baseColor = vec3(0.4, 0.9, 1.0);

  vec3 l = normalize(lightDirection);
  vec3 n = normalize(fragN);
  vec3 v = normalize(camPos - fragPos);

  vec3 h = normalize(v + l);

  float Id = clamp(dot(n, l), 0.0, 1.0);
  float Is = pow(clamp(dot(n, h), 0.0, 1.0), s);
  vec3 color = ((kA * Ia + kD * Id) * baseColor + (kS * Is) * vec3(1.0)) * lightColor;
  fragColor = vec4(color, 1.0);
}

vertex.source:
#version 300 es

// Vertex coordinates in object space for the render quad
in vec3 vertexPosition;
// Texture coordinate for this vertex and the render quad
in vec2 vertexTextureCoordinates;

// Texture coordinate needs to be passed on to the R2T fragment shader
out vec2 fragmentTextureCoordinates;

// Main program for each vertex of the render quad
void main() {
  gl_Position = vec4(vertexPosition, 1.0);
  fragmentTextureCoordinates = vertexTextureCoordinates;
}

fragment.source:
#version 300 es

precision highp float;

// A texture sampling unit, which is bound to the render quad texture buffer
uniform sampler2D textureRendered;

// Texture coordinates coming from the vertex shader, interpolated through the rasterizer
in vec2 fragmentTextureCoordinates;

uniform bool colorConversion;
out vec4 fragColor;

// Main program for each fragment of the render quad
void main() {
    if (colorConversion){

        vec4 lRGB = texture(textureRendered, fragmentTextureCoordinates);
        vec3 sRGB = vec3(0.0);

        // To avoid using a for loop one can use mix() to instantly check if the vector 
        // is less than equal, but this is not the naive implementation
        for (int i = 0; i < 3; i++){
            if (lRGB.rgb[i] <= 0.0031308){
                sRGB[i] = lRGB[i]* 12.92;
            } else {
                sRGB[i] = 1.055 * pow(lRGB.rgb[i], (1.0 / 2.4));
            }
        }
        // mix(1.055 * pow(color_sRgb, vec3(1.0 / 2.4)) - 0.055, color_sRgb * 12.92, lessThanEqual(color_sRgb, vec3(0.0031308)));

        vec4 color_sRGB = vec4(sRGB.rgb, lRGB.a);
        fragColor = color_sRGB;

    } else { fragColor = texture(textureRendered, fragmentTextureCoordinates);}
}
